mapper.select:
WITH ranked_data AS ( SELECT bl.historyno, bl.billno, c.customer, bt.bill_type, bl.bill_typeno, bl.customerno, bl.transactionymd, bl.transaction_amount, bl.updateuserid, ROW_NUMBER() OVER (PARTITION BY bl.billno ORDER BY bl.transactionymd DESC) AS row_num FROM ebillstorage.bill_log bl LEFT JOIN ebillstorage.review r ON bl.billno = r.billno LEFT JOIN ebillstorage.customer c ON bl.customerno = c.customerno LEFT JOIN ebillstorage.bill_type bt ON bl.bill_typeno = bt.bill_typeno WHERE r.review_status = 3 OR r.review_status = 4 ) SELECT historyno, billno, customer, bill_type, bill_typeno, customerno, transactionymd, transaction_amount, updateuserid, COUNT(*) OVER() AS totalBill FROM ranked_data WHERE (row_num = 1 OR row_num IS NULL)
AND transactionymd BETWEEN CAST(#{condition.transactionDateFrom} AS timestamp) AND CAST(#{condition.transactionDateTo} AS timestamp)
AND transaction_amount BETWEEN #{condition.transactionAmountFrom} AND #{condition.transactionAmountTo}
AND transaction_amount >= #{condition.transactionAmountFrom}
AND transaction_amount BETWEEN 0 AND #{condition.transactionAmountTo}
AND customerno = #{condition.customerno}
AND bill_typeno = #{condition.billTypeno}
AND updateuserid = #{condition.updateuserid}
ORDER BY transaction_amount
ORDER BY ${prop}
asc
desc
ORDER BY billno
asc
desc
LIMIT #{pagesize} OFFSET #{pageResult};

mapper.insert:
	BEGIN; 


	INSERT INTO ebillstorage.bill_log (billno, transactionymd, transaction_amount, customerno, bill_typeno, updateymd, updateuserid, remark)
    SELECT billno, transactionymd, transaction_amount, customerno, bill_typeno, updateymd, updateuserid, remark
	FROM ebillstorage.bill_input WHERE bill_input.billno=#{billno};

	update ebillstorage.review SET review_status = 3 WHERE review.billno=#{billno};

	DELETE FROM ebillstorage.bill_input WHERE bill_input.billno=#{billno};


	COMMIT;


	ROLLBACK;

mapper.update:
		BEGIN; 
		DELETE FROM ebillstorage.bill_input WHERE bill_input.billno=#{billno};

		update ebillstorage.review SET review_status = 4 WHERE review.billno=#{billno};



		COMMIT;

		BEGIN; 





    COMMIT;

mapper.insert:
BEGIN;
INSERT INTO ebillstorage.bill_input ( billno, transactionymd, transaction_amount, customerno, bill_typeno, updateymd, updateuserid, remark ) VALUES ( #{billno}, #{transactionymd}, #{transactionAmount}, #{customerno}, #{billTypeno}, NOW(), #{updateuserid}, #{remark} )
INSERT INTO ebillstorage.review (billno, review_status, edit_permissions) VALUES (#{billno},1,false)
INSERT INTO ebillstorage.bill_file (billno, bill_filelink) VALUES (#{billno},#{imageUrl})
COMMIT;
ROLLBACK;

    BEGIN; 
    INSERT INTO ebillstorage.bill_input (billno,transactionymd,transaction_amount,customerno,bill_typeno,updateymd,updateuserid,remark)
    VALUES (#{billno},#{transactionymd},#{transactionAmount},#{customerno},#{billTypeno},NOW(),#{updateuserid},#{remark});


	update ebillstorage.review SET review_status = 2,edit_permissions=false WHERE review.billno=#{billno};


	COMMIT;
	ROLLBACK;
